def min_operations(nums, target):
    need = [a - b for a, b in zip(nums, target)]
    last = [0, 0]
    ans = 0
    print(need)
    for c in need:
        # print(f'start: {c=} {last=}')
        if last[0] <= c <= last[1]:
            if c <= 0:
                last[0] = max(c, last[0])
                last[1] = 0
            else:
                last[1] = min(c, last[1])
                last[0] = 0
            # print(f'after {c=} {ans=} {last=}\n\n')
            continue 



        if c > last[1]:
            ans += c - last[1]
            last = [0, c]
        elif c < last[0]:
            ans += last[0] - c 
            last = [c, 0]
        # print(f'after {c=} {ans=} {last=}\n\n')
    return ans

class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        return min_operations(nums, target)
