class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n=nums.size();
        long long inc=0;
        long long dec=0;
        long long sum=0;
        for(int i=0;i<n;i++){
            long long t=nums[i]-target[i];
            if(t>0){
                if(t>dec){
                    sum+=t-dec;
                    dec=t;

                }
                else{
                    dec=t;
                }
                inc=0;
            }
            else if(t==0){
                inc=0;
                dec=0;
            }
            else{
                long long y=abs(t);
                if(y>inc){
                    sum+=y-inc;
                    inc=y;

                }
                else{
                    inc=y;
                }
                dec=0;
            }

        }
        return sum;
    }
};
