class Solution {
    public long minimumOperations(int[] nums, int[] target) {
        long ans=0;
        int ind=0;
        int n = nums.length;
        int[] arr = new int[n];
        for(int i=0; i<n; i++) {
            arr[i] = nums[i]-target[i];
        }

        boolean curr;
        if(arr[0] > 0) curr=true;
        else curr=false;

        for(int i=1; i<n; i++) {
            if(arr[i]>0 && !curr) {

                for(int k=ind; k<i; k++) arr[k]*=-1;
                ans += solve(ind , i , arr);

                ind=i;
                curr = true;
                continue;
            }
            if(arr[i] < 0 && curr) {
                ans += solve(ind, i , arr);
                ind =i;
                curr=false;
            }
        }
        if(!curr) {
            for(int k=ind; k<n; k++) arr[k]*=-1;
        }
        ans += solve(ind , n , arr);
        return ans;
    }

    public long solve(int i, int j, int[] arr) {
        long ans=0;
        while(i<j) {
            if(arr[i] == 0) {
                i++;
                continue;
            }
            int min = arr[i];
            int l = i+1;
            for (; l < j; l++) {
                if(arr[l] == 0) break;
                min = Math.min(arr[l],min);
            }
            ans+=min;
            for (int m = i; m < l; m++) {
                arr[m]-=min;
            }
        }
        return ans;
    }
}
