class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        long long ret = 0;
        vector<int> diff(n);
        for (int i = 0; i < n; i++) {
            diff[i] = target[i] - nums[i];
        }
        long long leftmax = abs(diff[0]);
        bool isneg = diff[0] >= 0 ? false : true;
        ret += leftmax;
        bool hasdec = false;
        for (int i = 1; i < n; i++) {
            bool temp = diff[i] >= 0 ? false : true;
            if (diff[i] == 0) {
                leftmax = 0;
                hasdec = false;
                continue;
            }
            if (diff[i - 1] == 0) {
                isneg = temp;
                leftmax = abs(diff[i]);
                ret += leftmax;
                hasdec = false;
                continue;
            }
            if (temp == isneg) {
                if (abs(diff[i]) == abs(diff[i - 1])) {
                    hasdec = false;
                    continue;
                }
                if (abs(diff[i]) < abs(diff[i - 1])) {
                    hasdec = true;
                    continue;
                }
                if (abs(diff[i]) > abs(diff[i - 1])) {
                    ret += abs(diff[i]) - abs(diff[i - 1]);
                    continue;
                }
            } else {
                isneg = temp;
                ret += abs(diff[i]);
                hasdec = false;
                continue;
            }
        }
        return ret;
    }
};
