func minimumOperations(nums []int, target []int) int64 {
	n := len(nums)
	cnt := make([]int, n)
	for i, v := range nums {
		cnt[i] = target[i] - v
	}
	pre, res := 0, 0
	for i, v := range cnt {
        if i > 0 {
		    pre = cnt[i-1]
        }
		if v*pre <= 0 {
			res += abs(pre)
		} else {
			if pre < 0 && v > pre {
				res += v - pre
			} else if pre > 0 && v < pre {
				res += pre - v
			}
		}
		if i == n-1 {
			res += abs(v)
		}
	}
	return int64(res)
}

func abs(x int) int {
	if x < 0 {
		return -x
	}
	return x
}
