using ll = long long;

class Solution {
private:
  ll getOp(vector<int> diff) {
    int n = (int)diff.size();
    ll res = 0;
    for(int i = 0; i < n; i++) {
      if(diff[i] >= 0) {
        if(i == 0) {
          // cout << i << " " << diff[i] << endl;
          res += diff[i];
        } else {
          if(diff[i] > diff[i - 1]) {
            res += diff[i] - max(diff[i - 1], 0);
            // cout << i << " " << diff[i] - max(diff[i - 1], 0) << endl;
          }
        }
      }
    }
    return res;
  }

public:
  ll minimumOperations(vector<int>& nums, vector<int>& target) {
    int n = (int)nums.size();
    vector<int> diff(n, 0);
    for(int i = 0; i < n; i++) {
      diff[i] = target[i] - nums[i];
      // cout << diff[i] << " ";
    }
    // cout << endl;
    ll opCnt1 = getOp(diff);
    for(int i = 0; i < n; i++) {
      diff[i] *= -1;
      // cout << diff[i] << " ";
    }
    // cout << endl;

    ll opCnt2 = getOp(diff);
    return opCnt1 + opCnt2;
  }
};
