class Solution {
    using ll = long long;
public:
    ll f(vector<ll>& nums) {
        ll operations = 0;
        ll prev = 0;

        for (int num : nums) {
            if (num > prev) {
                operations += num - prev;
            }
            prev = num;
        }

        return operations;
    }    
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<ll> a(n), b(n);
        for (int i = 0; i < n; ++i) {
            ll d = nums[i] - target[i];
            if (d > 0) {
                a[i] = d;
            } else {
                b[i] = -d;
            }
        }
        return f(a) + f(b);
    }
};
