typedef long long ll;
class Solution {
public:
    ll get(vector<int>& diff, int x, int y)
    {
        int ans = abs(diff[x]);
        for (int i = x + 1; i < y; i++)
        {
            int b = abs(diff[i]);
            int a = abs(diff[i - 1]);
            ans += max(b - a, 0);
        }
        //cout << x << "," << y << "," << ans << endl;
        return ans;
    }
    bool same(ll a, ll b)
    {
        if (a > 0 && b < 0) return false;
        if (b > 0 && a < 0) return false;
        return true;
    }
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<int> diff(n);
        for (int i = 0; i < n; i++) diff[i] = target[i] - nums[i];
        int i = 0;
        ll ans = 0;
        while (i < n)
        {
            int x = i;
            int c = diff[x];
            while (i < n && same(c, diff[i]))
            {
                if (c == 0) c = diff[i];
                i++;
            }
            //cout << i << endl;
            ll cur = get(diff, x, i);
            ans += cur;
        }
        return ans;


    }
};
