class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n=nums.size();
        // stack<int>st;
        long long ans=0;
        int pre=0;
        for(int i=0;i<n;i++){
            int d=target[i]-nums[i];
            if((pre>0&&d<0)||(pre<0&&d>=0))pre=0;
            if(d>0&&pre<d)ans+=d-pre;
            else if(d<0&&pre>d)ans+=pre-d;
            pre=d;
        }
        return ans;
    }
};
