class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n= nums.size();
        vector<int> diff(n);
        for(int i=0;i<n;i++)
        {
            diff[i]= target[i]-nums[i];
        }

        int prev=0;
        long long  ans=0;
        for(int i=0;i<n;i++)
        {
            if(diff[i]>=0)
            {
                if(prev<0)
                prev=0;
                if(diff[i]-prev>0)
                ans+= diff[i]-prev;
                prev=diff[i];
            }
            else
            {
                if(prev>0)
                prev=0;
                if(abs(diff[i])-abs(prev)>0)
                ans+= abs(diff[i])-abs(prev);
                prev= diff[i];
            }
        }
        return ans;

    }
};
