public class Solution
{
    public long MinimumOperations(int[] nums, int[] target)
    {
        double CalculateMinOps(int[] nums, int[] target)
        {
            double[] d = new double[nums.Length];

            int i = 0;
            while (i < nums.Length)
            {
                d[i] = target[i] - nums[i];
                i++;
            }

            double totOps = 0;
            double curSegDiff = 0;

            i = 0;
            while (i < nums.Length)
            {
                if (!(i == 0 || (d[i - 1] > 0 && d[i] > 0) || (d[i - 1] < 0 && d[i] < 0)))
                {
                    totOps += Math.Abs(d[i]);
                    curSegDiff = d[i];
                }
                else
                {
                    double addOps = Math.Abs(d[i]) - Math.Abs(curSegDiff);
                    if (addOps <= 0)
                    {
                    }
                    else totOps += addOps;
                    curSegDiff = d[i];

                }
                i++;
            }

            return totOps;
        }

        return (long)CalculateMinOps(nums, target);
    }
}
