class Solution {
    public long minimumOperations(int[] nums, int[] target) {
        long[] diff = new long[nums.length];

        for(int i=0; i<nums.length; i++) {
            diff[i] = target[i]-nums[i];
        }

        long ans = 0;
        long lastDiff = 0;
        for(int i=0; i<nums.length; i++) {
            if(diff[i]==lastDiff) {
                //doNothing
            }else if(diff[i]*lastDiff < 0) {
                ans += Math.abs(diff[i]);
            }else if(Math.abs(diff[i]) > Math.abs(lastDiff)) {
                ans += Math.abs(diff[i]) - Math.abs(lastDiff);
            }else {
                //doNothing
            }
            lastDiff = diff[i];

            // System.out.println(ans);
        }

        return ans;
    }
}
