class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {

        ios_base::sync_with_stdio(false);
        cin.tie(NULL);
        int n = nums.size();
        vector<long long> diff(n);
        for (int i = 0; i < n; ++i) {
            diff[i] = target[i] - nums[i];
        }

        long long res = 0;
        long long curr = 0;

        for (int i = 0; i < n; ++i) {
            if (i == 0 || (diff[i - 1] > 0 && diff[i] > 0) || (diff[i - 1] < 0 && diff[i] < 0)) {
                int req = abs(diff[i]) - abs(curr);
                if (req > 0) {
                    res += req;
                }
                curr = diff[i];
            } else {
                res += abs(diff[i]);
                curr = diff[i];
            }
        }

        return res;
    }
};
