class Solution {
    public long minimumOperations(int[] nums, int[] target) {
        int n = nums.length;
        int[] array = new int[n + 1];
        int[] array2 = new int[n + 1];
        long x = 0,y = 0;
        for(int i = 1;i <= n;i++){
            array[i] = target[i - 1] - nums[i - 1];
        }
        for(int i = 1;i <= n;i++){
            array2[i] = array[i] - array[i - 1];
            if(array2[i] > 0) x += array2[i];
            else y -= array2[i];
        }

        // System.out.println(x + " " + y);
        return Math.min(x,y) + Math.abs(x - y);
    }
}
