class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<long long> ve1(n + 1) , ve2(n + 1);
        long long answer = 0;
        for (int i = 1; i <= n; i++){
            if (target[i - 1] - nums[i - 1] >= 0) {
                ve1[i] = target[i - 1] - nums[i - 1];
            }
            else {
                ve2[i] = abs(target[i - 1] - nums[i - 1]);
            }

        }
        n++;
        for (int i = 0; i < n - 1; i++) {
            if (ve1[i + 1] > ve1[i]) {
                answer += (ve1[i + 1] - ve1[i]);
            }
            if (ve2[i + 1] > ve2[i]) {
                answer += (ve2[i + 1] - ve2[i]);
            }
        }
        return answer;
    }
};
