class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        free_d = 0
        res = 0

        for n, t in zip(nums, target):
            if n < t:
                free_d = max(free_d, 0)
                if n + free_d < t:
                    res += t - (n + free_d)
                    free_d += t - (n + free_d)
                else:
                    free_d = t - n
            elif n > t:
                free_d = min(free_d, 0)
                if n + free_d > t:
                    res += abs(t - (n + free_d))
                    free_d += t - (n + free_d)
                else:
                    free_d = t - n
            else:
                free_d = 0

        return res
