class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n=nums.size();
        vector<int> help(n,0);
        for(int i=0; i<nums.size(); i++){
            help[i] = target[i]-nums[i];
        }
        long long ans=abs(help[0]);
        for(int i=1; i<n; i++){
            if(help[i-1] == help[i]) continue;
            if(help[i] >0 && help[i-1] >0 && help[i]> help[i-1]) ans += help[i]- help[i-1];
            else if(help[i] >0 && help[i-1] >0 && help[i] < help[i-1]) continue;
            else if(help[i]<0 && help[i-1]<0 && help[i] < help[i-1]) ans += abs(help[i]-help[i-1]);
            else if(help[i]<0 && help[i-1]<0 && help[i] >= help[i-1]) continue;
            else  ans += abs(help[i]);

        }
        return ans;
        // int min= *min_element(help.begin(), help.end());
        // int max= *max_element(help.begin(), help.end());
        // return abs(min - max) +1;
    }
};
