#define ll long long
class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n=nums.size();
        vector<ll> dif(n);
        for(int i=0;i<n;i++){
            dif[i]=target[i]-nums[i];
        }
        ll c=0,cnt=0,cur=0;
        for(int i=0;i<n;i++){
            if(i==0 || (dif[i-1]>0 && dif[i]>0) || (dif[i-1]<0 && dif[i]<0)){
                c=abs(dif[i])-abs(cur);
                if(c>0){
                    cnt+=c;
                } 
                cur=dif[i];
            }
            else{
                cnt+=abs(dif[i]);
                cur=dif[i];
            }
        }
        return cnt;
    }
};
