long long s[100001];
class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        int i;
        for (i = 0; i < n; i++) {
            s[i] = target[i] - nums[i];
        }
        long long ans = abs(s[0]);
        for (i = 1; i < n; i++) {
            if (s[i - 1] < 0) {
                if (s[i] < 0) {
                    if (abs(s[i]) > abs(s[i-1])) {
                        ans += (abs(s[i]) - abs(s[i-1]));
                    }
                } else {
                    ans += abs(s[i]);
                }
            } else {
                if (s[i] > 0) {
                    if (abs(s[i]) > abs(s[i-1])) {
                        ans += (abs(s[i]) - abs(s[i-1]));
                    }
                } else {
                    ans += abs(s[i]);
                }
            }
        }
        return ans;
    }
};
