class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n=nums.size();
        vector<long long>diff(nums.size());
        for(int i=0;i<n;i++)diff[i] = target[i] - nums[i];
        long long ans=0,x = 0;
        for (int i=0;i<diff.size();i++) {
            if (i == 0 || (diff[i-1]>0 && diff[i]>0) || (diff[i-1]<0 && diff[i]<0)) {
                int temp=abs(diff[i])-abs(x);
                if (temp>0)ans += temp;
                x = diff[i];
            } else {
                ans+=abs(diff[i]);x = diff[i];
            }
        }return ans;
    }
};
