class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        for(int i = 0; i < n; ++i){
            target[i] -= nums[i];
        }
        long long ans = 0;
        for(int i = 0; i < n; ){
            int j = i;
            while(j + 1 < n && ((target[j] >= 0 && target[j + 1] >= 0)||(target[j] < 0 && target[j + 1] < 0 ))){
                ++j;
            }
            target[i] = abs(target[i]);
            ans += target[i];
            // cout << i << " " << j << endl;
            for(int k = i + 1; k <= j; ++k){
                if(target[k] < 0)target[k] = -target[k];
                ans += max(0, target[k] - target[k - 1]);
            }
            i = j + 1;
        }
        return ans;
    }
};
