class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
       vector<long long> temp(nums.size());
        for (int i = 0; i < nums.size(); ++i) {
            temp[i] = target[i] - nums[i];
        }

        long long ans = 0;
        long long dis = 0;

        for (int i = 0; i < temp.size(); ++i) {
            if(i==0){
                ans+=abs(temp[i]);
                dis=temp[i];
                continue;
            }
            if ((temp[i - 1] < 0 && temp[i] < 0) || (temp[i - 1] > 0 && temp[i] > 0) ) {
                int op = abs(temp[i]) - abs(dis);
                if (op > 0) {
                    ans += op;
                }
                dis = temp[i];
            } else {
                ans += abs(temp[i]);
                dis = temp[i];
            }
        }

        return ans;
    }
};
