class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long sol = 0;
        int flag = 0;
         if(nums[0] > target[0]){
            flag = 1;
         }
         else if(nums[0] == target[0]){
            flag = 0;
         }
         else{
            flag = -1;
         }

        cout<<flag<<endl;
        long long operation = abs(nums[0]-target[0]);
        long long temp = operation;

        for(int i = 1; i<nums.size(); i++){
            int ele1 = nums[i];
            int ele2 = target[i];


            if(ele1 > ele2 && flag == 1){
               int opar = ele1 - ele2;

               if(temp<opar){
                  operation += (opar-temp);
                  temp = opar;

               }
               else{
                 temp = opar;
               }

            }
            else if(ele1 == ele2 && flag == 0){
                operation = 0;
            }
            else if(ele1 < ele2 && flag == -1){
              int opar = ele2 - ele1;
               cout<<opar<<endl;
               if(temp<opar){
                  operation += (opar-temp);
                  temp = opar;

               }
               else{
                 temp = opar;
               }

            }
            else{
                sol+=operation;

                if(nums[i] > target[i]){
                 flag = 1;
                }
                else if(nums[i] == target[i]){
                    flag = 0;
                }
                else{
                    flag = -1;
                }


                   operation = abs(nums[i]-target[i]);
                   temp = operation;

            }
        }

        sol+=operation;

        return sol;
    }
};
