class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<int> tmp(n);
        for(int i = 0;i < n;i++){
            tmp[i] = target[i] - nums[i];
            //该位置假设加了x次,减少了y次，则保证x-y=tmp[i]
        }
        vector<int> d(n + 1);
        d[0] = tmp[0];
        for(int i = 1;i < n;i++){
            d[i] = tmp[i] - tmp[i - 1];
        }
        //问题转化为了将全部都是0的数组，变为上述差分数组的最小步数
        //每次可以选两个数，一个加1另一个减去1
        long long cnt = 0;
        //先看有哪些数可以互补填写
        vector<int> z1,z2;
        for(int i = 0;i < n;i++){
            if(d[i] < 0) z1.push_back(d[i]);
            if(d[i] > 0) z2.push_back(d[i]);
        }

        while(z1.size() && z2.size()){
            int x = z1.back();z1.pop_back();
            int y = z2.back();z2.pop_back();
            int r = x + y;
            if(r < 0) z1.push_back(r);
            else if(r > 0) z2.push_back(r);
            cnt += min(abs(x),abs(y));
           // cout << x << " " << y << " " << min(abs(x),abs(y)) << endl;
        }
        for(auto x : z1){
            cnt += abs(x);
        }
        for(auto y : z2){
            cnt += y;
        }
        return cnt;
    }
};
