class Solution {
public:
    long long minimumOperations(vector<int>& n, vector<int>& t) {
       vector<long long> d;
        for (int i = 0; i < n.size(); ++i) d.push_back(t[i] - n[i]);
        long long o = 0, c = 0;
        for (int i = 0; i < d.size(); ++i) {
            if (i == 0 || (d[i-1] > 0 && d[i] > 0) || (d[i-1] < 0 && d[i] < 0)) {
                long long a = abs(d[i]) - abs(c);
                if (a > 0) o += a;
                c = d[i];
            } else {
                o += abs(d[i]);
                c = d[i];
            }
        }
        return o;
    }
};
