class Solution {
public:
    // 4 5 6 5 3
    //  4 5 6 +6
    // +6 5
    // +6 3

    // 4 5 6 
    // +6 3
    // +6 3 4 1 2
    // +6 +（4 - 1)


    // 1 2 3 2 1
    long long calc(vector<int>& nums) {
        long long res = 0;
        stack<int> stk;
        for (int i = 0; i < nums.size(); ++i) {
            int v = max(0, nums[i]);
            // cout << v << ',';
            if (!stk.empty() && stk.top() >= v) {
                res += stk.top() - v;
                while (!stk.empty() && stk.top() >= v) {
                    stk.pop();
                }
            }
            stk.push(nums[i]);
        }
        // cout << endl;
        // cout << res << endl;
        return res;
    }

    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        for (int i = 0; i < target.size(); ++i) {
            target[i] -= nums[i];
        }
        target.push_back(0);

        long long a = calc(target);
        for (int i = 0; i < target.size(); ++i) {
            target[i] = -target[i];
        }
        return a + calc(target);
    }
};
