class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        n = len(nums)
        s = [x - y for x, y in zip(nums, target)]
        d = [s[0]] + [s[i] - s[i - 1] for i in range(1, n)]
        pos, neg = 0, 0
        for i in range(n):
            if d[i] > 0:
                pos += d[i]
            else:
                neg -= d[i]
        return max(pos, neg)
