class Solution {
public:
    #define ll long long
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        ll n=nums.size();

        vector<ll>dp(n,0);
        for(ll i=0;i<n;i++){
            dp[i]=(target[i]-nums[i]);
        }

         ll sum=0;
        for(ll i=0;i<n;i++){
            if(i==0){
                sum+=abs(dp[i]);
            continue;}


           if((dp[i-1]>=0 && dp[i]<0)||(dp[i-1]<=0 && dp[i]>0))
               sum+=abs(dp[i]);
            else if(dp[i-1]<0 && dp[i]<0){
                if(abs(dp[i-1])>=abs(dp[i]))
                    sum+=0;
                else
                    sum+=abs(dp[i])-abs(dp[i-1]);
            }
            else if(dp[i-1]>=0 && dp[i]>0){
                if(dp[i-1]>=dp[i])
                    sum+=0;
                else
                    sum+=(dp[i]-dp[i-1]);
            }

        }

        return sum;

    }
};
