class Solution {
public:
    long long solve(vector<int> &nums,int l,int r)
    {
        int mini=abs(nums[l]);
        long long result=0;
        for(int i=l;i<=r;i++)
        {
            nums[i]=abs(nums[i]);
            mini=min(mini,nums[i]);
        }
        result+=mini;
        int prev=l;
        for(int i=l;i<=r;i++)
        {
            nums[i]-=mini;
            if(nums[i]==0)
            {
                if(i!=prev) result+=solve(nums,prev,i-1);
                prev=i+1;
            }
        }
        if(prev!=r+1) result+=solve(nums,prev,r);
        return result;
    }
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n=nums.size();
        for(int i=0;i<nums.size();i++)
        {
            nums[i]=nums[i]-target[i];
        }

        int prev=0;
        long long result=0;
        for(int i=0;i<n;i++)
        {
            if( (nums[prev]<0 && nums[i]<0) || (nums[prev]>0 && nums[i]>0)) continue;
            else 
            {
                if(prev!=i)
                    result+=solve(nums,prev,i-1);
                if(nums[i]==0) prev=i+1;
                else prev=i;
            }
        }
        if(prev!=n)
            result+=solve(nums,prev,n-1);

        return result;
    }
};
