class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n=nums.size();
        vector<int> diff(n, 0);

        for(int i=0; i<n; i++)
            diff[i]=target[i]-nums[i];

        long long ans = 0,  currDiff = 0;

        for (int i = 0; i < diff.size(); i++) 
        {
            long long x=abs(diff[i]) - abs(currDiff);

            bool pos=0;
            bool neg=0;

            if(i>0) pos=(diff[i - 1] > 0 && diff[i] > 0);
            if(i>0) neg=(diff[i - 1] < 0 && diff[i] < 0);

            if (i == 0) 
                ans += (x > 0) ? x : 0;
            else if(pos || neg)
                ans += (x > 0) ? x : 0;
            else 
                ans += abs(diff[i]);

            currDiff = diff[i];
        }

        return ans;


    }
};
