class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size() ;
        vector<long long> diff ;
        for(int i = 0 ; i <= n - 1 ; i++){
            diff.push_back(target[i] - nums[i]) ;
        }
        long long ans = 0 , d = 0 ;
        for(int i = 0 ; i <= n - 1 ; i++){
            if((!i) || (diff[i] > 0 && diff[i - 1] > 0)){
                ans += (max(abs(diff[i]) - abs(d) , 0LL)) ;
            }
            else if((!i) || (diff[i] < 0 && diff[i - 1] < 0)){
                ans += (max(abs(diff[i]) - abs(d) , 0LL)) ;
            }
            else{
                ans += abs(diff[i]) ;
            }
            d = diff[i] ;
        }
        return ans ;
    }
};
