class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        # 差分，原题

        for i in range(len(nums)):
            nums[i] = target[i] - nums[i]
        print(f"原数组 nums = {nums}")

        b = [nums[0]]
        for i in range(0, len(nums) - 1):
            b.append(nums[i + 1] - nums[i])

        print(f"b = {b}")
        p, q = 0, 0

        for x in b:
            if x >= 0:
                p += x
            else:
                q -= x
        print(f"p = {p}, q = {q}")
        # return max(p - q, 0) + min(p, q)
        return max(p, q)
