class Solution {
public:
#define ll long long
    ll findMin(vector<int>&v){
        if(v.size()==0)return 0;
        ll mi = 1e12;
        map<ll,ll>m;
        for(int i=0;i<v.size();i++){
            // cout << v[i] << " ";
            v[i]=abs(v[i]);
            m[v[i]]++;
            mi = min(mi,(ll)v[i]);
        }
        // cout << "\n";
        if(m.size()==1)return mi;
        ll ans = 0;
        vector<int>v1;
        for(int i=0;i<v.size();i++){
            if(v[i]==mi){
                if(v1.size()>0){
                    ans += findMin(v1);
                    v1.clear();
                }
            }
            else{
                v1.push_back(v[i]-mi);
            }
        }
        if(v1.size()>0)ans+=findMin(v1);
        v1.clear();
        return ans+mi;
    }
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans = 0;
        for(int i=0;i<nums.size();i++){
            target[i]-=nums[i];
        }
        vector<int>v;
        for(int i=0;i<target.size();i++){
            if(target[i]==0){
                if(v.size()>0){
                ans+=findMin(v);
                v.clear();
                }
            }
            if(target[i]>0){
                if(v.empty()){
                    v.push_back(target[i]);
                }
                else{
                    if(v[v.size()-1]>0)v.push_back(target[i]);
                    else{
                        ans += findMin(v);
                        v.clear();
                        v.push_back(target[i]);
                    }
                }
            }
            else{
                if(v.empty()){
                    v.push_back(target[i]);
                }
                else{
                    if(v[v.size()-1]<0)v.push_back(target[i]);
                    else{
                        ans += findMin(v);
                        v.clear();
                        v.push_back(target[i]);
                    }
                }
            }
        }
        if(v.size()>0)ans+=findMin(v);
        return ans;
    }
};
