class Solution {
    public long minimumOperations(int[] nums, int[] target) {
        int n = nums.length;
        long result = 0;
        int prev = 0;
        for(int i = 0; i < n; i++) {
            int diff = nums[i] - target[i];
            if(diff > 0) {
                if(prev < 0) prev = 0;
                if(diff > prev) result += diff - prev;
            }
            else if(diff < 0) {
                if(prev > 0) prev = 0;
                if(diff < prev) result += prev - diff;
            }
            prev = diff;
        }
        return result;
    }
}
