class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long n = nums.size(), ans = 0, mx1 = 0, mx2 = 0;
        vector<int> vec(n, 0);
        for(int i=0;i<n;i++){
            if(target[i]>nums[i]) vec[i] = 1;
            else if(target[i]<nums[i]) vec[i] = -1;
        }
        for(int i=0;i<n;i++){
            if(vec[i] == 1){
                int x = target[i]-nums[i];
                if(x>mx1){
                    ans += x-mx1;
                    mx1 = x;
                } 
                else{
                    mx1 = x;
                }
                mx2 = 0;
            }
            else if(vec[i] == 0){
                mx1 = 0;
                mx2 = 0;
            }
            else{
                int x = nums[i]-target[i];
                if(x>mx2){
                    ans += x-mx2;
                    mx2 = x;
                } 
                else{
                    mx2 = x;
                }
                mx1 = 0;
            }
        }
        return ans;
    }
};
