class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans=0;
        stack<int> st1, st2;
        for(int i=0;i<nums.size();i++)nums[i]-=target[i];
        for(int i=0;i<nums.size();i++){
            if(nums[i]>=0){
                while(st2.size()>0 && st2.top() <= 0){
                    int x = st2.top();
                    st2.pop();
                    if(st2.size()>0 && st2.top()<= 0){
                        ans+=st2.top() - x;
                    }else{
                        ans-=x;
                    }
                }
                while(st1.size()>0 && st1.top() >= nums[i]){
                    int x = st1.top();
                    st1.pop();
                    if(st1.size()>0 && st1.top()>=nums[i]){
                        ans+=x-st1.top();
                    }else{
                        ans+=x-nums[i];
                    }
                }
                st1.push(nums[i]);
            }else{
                while(st1.size()>0 && st1.top() >= 0){
                    int x = st1.top();
                    st1.pop();
                    if(st1.size()>0 && st1.top()>=0){
                        ans+=x-st1.top();
                    }else{
                        ans+=x;
                    }
                }
                while(st2.size()>0 && st2.top() <= nums[i]){
                    int x = st2.top();
                    st2.pop();
                    if(st2.size()>0 && st2.top()<= nums[i]){
                        ans+=st2.top() - x;
                    }else{
                        ans+=nums[i]-x;
                    }
                }
                st2.push(nums[i]);
            }
        }
        while(st2.size()>0 && st2.top() <= 0){
            int x = st2.top();
            st2.pop();
            if(st2.size()>0 && st2.top()<= 0){
                ans+=st2.top() - x;
            }else{
                ans-=x;
            }
        }
        while(st1.size()>0 && st1.top() >= 0){
            int x = st1.top();
            st1.pop();
            if(st1.size()>0 && st1.top()>=0){
                ans+=x-st1.top();
            }else{
                ans+=x;
            }
        }
        return ans;
    }
};
