class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:

        leng = len(nums)

        res = abs(target[0] - nums[0])
        op = target[0] - nums[0]

        for i in range(1, leng):
            n = nums[i]
            t = target[i]
            diff = t - n
            # print(res)
            if diff * op <= 0:
                op = diff
                res += abs(diff)
            else:
                pop = op
                op = diff
                if abs(op) <= abs(pop):
                    continue
                res += max(abs(op), abs(pop)) - min(abs(op), abs(pop))

        return res
