class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long res = 0;
        int n = nums.size();
        vector<int>d(n,0);
        for(int i = 0;i<n;i++){
            d[i]=target[i]-nums[i];
        }
        res = abs(d[0]);
        for(int i = 1;i<n;i++){
            if(d[i]==0){
                continue;
            }
            else if(d[i-1]<=0 && d[i]>0){
                res+=d[i];
            }
            else if(d[i-1]>=0 && d[i]<0){
                res+=abs(d[i]);
            }
            else if(d[i-1]<=0 && d[i-1]>d[i]){
                res+=(d[i-1]-d[i]);
            }
            else if(d[i-1]>=0 && d[i-1]<d[i]){
                res+=(d[i]-d[i-1]);
            }
        }
        return res;
    }
};
