#define ll long long
class Solution {
public:
    long long calc(vector<long long>&v, int st=0,int end=-1) {
        int n=v.size();
        if(n==0)return 0;
        if(end==-1)end=n-1;
        if(st>end)return 0;
        ll mini = INT_MAX;
        for(int i=st;i<=end;i++) {
            mini=min(mini,v[i]);
        }
        long long ans=mini;
        for(int i=st;i<=end;i++) {
            v[i]-=mini;
        }
        int preI=st;
        for(int i=st;i<=end;i++) {
            if(v[i]==0) {
                if(preI<=i-1)
                ans+=calc(v,preI,i-1);
                int j=i;
                while(j<n and v[j]==0)j++;
                i=j-1;
                preI=j;
            }
        }
        if(preI<=end)
        ans+=calc(v,preI,end);
        return ans;
    }
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n=nums.size();
        for(int i=0;i<n;i++)nums[i]=target[i]-nums[i];
        long long ans=0;
        for(int i=0;i<n;)
        {
            if(nums[i]==0){i++;continue;}
            int j=i;
            vector<long long>v;
            while(j<n and ((long long)nums[j])*((long long)nums[i])>0){
                if(nums[j]<0)v.push_back(-nums[j]);
                else v.push_back(nums[j]);
                j++;}
            //i..j-1
            ans+=calc(v);
            //cout<<i<<' '<<j-1<<' '<<ans<<endl;
            i=j;
        }
        return ans;
    }
};
