class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        n = len(nums)
        for i in range(n):
            nums[i] = target[i] - nums[i]

        pre = 0
        ans = 0
        for num in nums:
            if (pre > 0 and num < 0) or (pre < 0 and num > 0):
                ans += abs(num)
            else:
                ans += max(abs(num) - abs(pre), 0)
            pre = num
        return ans
