class Solution {
    public long minimumOperations(int[] nums, int[] target) {
        for (int i = 0; i < nums.length; i++) {
            nums[i] -= target[i];
        }

        int diff = nums[0];
        long res = Math.abs(diff);
        for (int i = 1; i < nums.length; i++) {
            if ((diff > 0 && nums[i] > 0) || (diff < 0 && nums[i] < 0)) {
                if (Math.abs(diff) < Math.abs(nums[i])) {
                    res += Math.abs(nums[i]) - Math.abs(diff);
                }
            } else {
                res += Math.abs(nums[i]);
            }
            diff = nums[i];
        }
        return res;
    }
}
