class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {

        int n=nums.size();
        vector<int> res(n);

        for(int i=0;i<n;i++){
            res[i]=target[i]-nums[i];
        }

        int i=0;


        long long ans=0;
        while(i<n){

            vector<int> temp1,temp2;
            while(i<n && res[i]>0){
                temp1.push_back(res[i]);
                i++;
            }

            while(i<n && res[i]==0){
                i++;
            }

            while(i<n && res[i]<0){
                temp2.push_back(abs(res[i]));
                i++;
            }

            int s1=temp1.size(),s2=temp2.size();

            if(s1){
                ans+=temp1[0];
                for(int i=1;i<s1;i++){
                    if(temp1[i]>temp1[i-1]){
                        ans+=(temp1[i]-temp1[i-1]);
                    }
                }   
            }

            if(s2){
                ans+=temp2[0];
                for(int i=1;i<s2;i++){
                    if(temp2[i]>temp2[i-1]){
                        ans+=(temp2[i]-temp2[i-1]);
                    }
                }   
            }
        }

        return ans;
    }
};
