class Solution {
    public long minimumOperations(int[] inputArr1, int[] inputArr2) {
       int arrSize = inputArr1.length;
        int[] diffArr = new int[arrSize];
        for (int index = 0; index < arrSize; index++) {
            diffArr[index] = inputArr2[index] - inputArr1[index];
        }

        int signFlag = 0;
        long currentMax = 0, result = 0;
        result = Math.abs(diffArr[0]);
        currentMax = result;

        if (diffArr[0] >= 0)
            signFlag = 1;

        for (int index = 1; index < arrSize; index++) {
            if (diffArr[index] < 0 && signFlag == 1) {
                result += Math.abs(diffArr[index]);
                currentMax = Math.abs(diffArr[index]);
                signFlag = 0;
                continue;
            }
            if (diffArr[index] >= 0 && signFlag == 0) {
                result += Math.abs(diffArr[index]);
                currentMax = Math.abs(diffArr[index]);
                signFlag = 1;
                continue;
            }
            if (currentMax >= Math.abs(diffArr[index])) {
                currentMax = Math.abs(diffArr[index]);
                continue;
            }
            else {
                result += Math.abs(diffArr[index]) - currentMax;
                currentMax = Math.abs(diffArr[index]);
            }
        }

        return result;
    }
}
