#define pb push_back
class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<int> diff(n);
        for(int i=0;i<n;++i){
            diff[i] = target[i] - nums[i];
        }
        vector<vector<int>> seg(n);
        int cur=0;
        int i = 0;
        while(i<n){
            if(diff[i]==0){
                ++i;
                continue;
            }
            while(i<n && diff[i]>0){
                seg[cur].pb(diff[i]);
                ++i;
            }
            if(cur<n && (int)seg[cur].size()>0)
                ++cur;
            while(i<n && diff[i]<0){
                seg[cur].pb(abs(diff[i]));
                ++i;
            }
            if(cur<n && (int)seg[cur].size()>0)
                ++cur;
        }
        long long ans = 0;
        for(int i=0;i<n;i++){
            int ssz = (int)seg[i].size();
            seg[i].resize(ssz);
            long long utilize = 0;
            for(int j=0;j<ssz;++j){
                if(seg[i][j]<=utilize){
                    utilize=seg[i][j];
                } else {
                    long long need = seg[i][j] - utilize;
                    ans+=need;
                    utilize+=need;
                }
            }
        }
        return ans;
    }
};
