class Solution {
public:
    long long minimumOperations(vector<int>& a, vector<int>& target) {
        using int64 = long long;
        int n = a.size();
        for (int i = 0; i < n; ++i) a[i] -= target[i];


        for (auto& x : a) cout << x << " ";
        cout << endl;
        auto solve1 = [&](const vector<int>& a) {
            int64 ret = max(a[0], 0);
            for (int i = 1; i < n; ++i) {
                ret += max(0, a[i] - a[i - 1]);
            }
            return ret;
        };
        auto solve2 = [&](const vector<int>& a) {
            int64 ret = max(-a[0], 0);
            for (int i = 1; i < n; ++i) {
                ret += max(0, -(a[i] - a[i - 1]));
            }
            return ret;
        };
        int64 ret = solve1(a);
        ret = max(ret, solve2(a));
        return ret;
    }
};
