class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        vector<int> diff (nums.size());
        for(int i = 0 ; i < nums.size(); i++){
            diff[i] =  target[i] - nums[i];
        }

        long long ans = 0;
        long long prev = 0;
        for(int i = 0 ; i < nums.size(); i++){
            long long cur = diff[i];
            if(cur == 0 || prev * cur < 0){
                prev = 0;
            }
            if(abs(prev) < abs(cur)){
                ans += abs(cur) - abs(prev);
            }
            prev = cur;
        }

        return ans;
    }
};
