class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n=nums.size();
        long long pos=0, neg=0;
        long long count=0;
        vector<long long>diff;
        for(int i=0; i<n; i++) diff.push_back(target[i]-nums[i]);

        for(auto i:diff){
            if(i>=0){
                if(i>pos) count+=(i-pos);
                pos=i;
                neg=0;  
            }else{
                if(abs(i)>neg) count+=(abs(i)-neg);
                neg=abs(i);
                pos=0;
            }
        }
        return count;
    }
};
