class Solution {
    public long minimumOperations(int[] nums, int[] target) {
        long ans = 0L;
        int[] nums2 = new int[nums.length];
        for (int i = 0, left = 0, right = 0, direction = 1; i <= nums.length; i++) {

            if (i == nums.length) {
                ans += getAns(nums2, left, right, direction);
                break;
            } else if (target[i] == nums[i]) {
                ans += getAns(nums2, left, right, direction);
                left = i;
            } else if (direction == 1 && target[i] < nums[i]) {
                ans += getAns(nums2, left, right, direction);
                left = i;
                direction = -1;
            } else if (direction == -1 && target[i] > nums[i]) {
                ans += getAns(nums2, left, right, direction);
                left = i;
                direction = 1;
            }

            right = i;
            nums2[i] = target[i] - nums[i];

        }
        // System.out.println(Arrays.toString(nums2));

        return ans;
    }

    private long getAns(int[] nums2, int left, int right, int direction) {
        // System.out.println(left + " " + right + " " + direction);
        long ans = 0L;
        if (direction > 0) {
            for (int i = left; i < nums2.length && i <= right; i++) {
                if (i == left) {
                    ans += nums2[i];
                } else if (nums2[i] > nums2[i - 1]) {
                    ans += nums2[i] - nums2[i - 1];
                }
            }
        } else {
            for (int i = left; i < nums2.length && i <= right; i++) {
                if (i == left) {
                    ans -= nums2[i];
                } else if (nums2[i] < nums2[i - 1]) {
                    ans -= nums2[i] - nums2[i - 1];
                }
            }
        }
        return ans;
    }
}
