class Solution {
public:

    #define ll long long

    ll solve(vector<int> & nums1, vector<int> & nums2, int i,int j){

        if(i == j){ return abs(nums1[i]-nums2[i]);}
        if(i > j){return 0;}
        int g = nums1[i] < nums2[i] ? 1 : 0;
        if(g){
        int maxi = 1e9;
        int k = i;

        while(k <= j && nums1[k] < nums2[k]){
            maxi= min(maxi,nums2[k]-nums1[k]);
            k++;
        }
        ll v1 = solve(nums1,nums2,k,j);

        v1 += maxi;

        int prev = i;
        for(int l=i; l<k; l++){
            nums1[l] += maxi;
            if(nums2[l] == nums1[l]){v1 += solve(nums1,nums2,prev,l-1); prev = l+1;}
        }
            v1 += solve(nums1,nums2,prev,k-1);

             return v1;
        }
        else{
             int maxi = 1e9;
        int k = i;

        while(k <= j && nums1[k] >= nums2[k]){
            maxi= min(maxi,nums1[k]-nums2[k]);
            k++;
        }
        ll v1 = solve(nums1,nums2,k,j);

        v1 += maxi;

        int prev = i;
        for(int l=i; l<k; l++){
            nums1[l] -= maxi;
            if(nums2[l] == nums1[l]){v1 += solve(nums1,nums2,prev,l-1); prev = l+1;}
        }
            v1 += solve(nums1,nums2,prev,k-1);

             return v1;

        }
       return 0;
    }


    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        return solve(nums,target,0,nums.size()-1);
    }
};
