class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long op = 0;
        long long t = 0;
        vector<int>store;
        int n = nums.size();
        for(int i=0;i<n;i++)
        {
            store.push_back(target[i]-nums[i]);
        }

        for(int i=0;i<n;i++)
        {
            if((store[i]<0 and t>0)||(store[i]>0 and t<0))
            {
                t = 0;
            }
            if((store[i]<=0 and store[i]-t>=0)|| (store[i]>=0 and store[i]-t<=0))
            {
                t = store[i];
            }
            else
            {
                op+=abs(store[i]-t);
                t+=(store[i]-t);
            }
        }
        return op;
    }
};
